/* === 定义主题颜色 === */
:root {
    --main-words: var(--SmartThemeBodyColor);
/* 对应主要文本 */
--yellow-words: var(--SmartThemeEmColor);
/* 对应斜体文本-->按键的黄色+user边框 */
--darkyellow: #B07B3A;
/*按钮被选中的颜色，没有多的颜色了*/
--red-words: var(--SmartThemeUnderlineColor);
/* 对应下划线文本-->char的红色边框+按钮文字*/
--green-words: var(--SmartThemeQuoteColor);
/* 对应引用文本+幽灵颜色+标题框框 */
--ui-background-main: var(--SmartThemeUserMesBlurTintColor);
/* 对应用户消息模糊色调*/
--ui-border-color: var(--SmartThemeBorderColor);
/*对应UI边框颜色 */
--ui-hover-color: var(--SmartThemeShadowColor); /*对应阴影颜色*/
--crimson70a: #A70C0C;
--white100: #FFFFFF;

--topBarIconSize: calc(var(--mainFontSize) * 1.5);
--bottomFormIconSize: calc(var(--mainFontSize) * 1.5);
--bottomFormBlockPadding: calc(var(--mainFontSize) / 2);
}

/*全局文本*/
p {
    font-size: 1.02em;
    line-height: 1.8;
}

#form_sheld {
    margin: 0 !important;
}
.script-group-header {
    background: var(--yellow-words) !important;
}
/* ===== 菜单栏样式修改 ===== */
/*去除菜单栏下的阴影 */
#top-bar {
  box-shadow: none !important; /* 移除顶部菜单栏的阴影 */
  border-radius: 6px 6px 0 0; /* 顶部圆角，美化外观 */
}

.drawer-icon.closedIcon {
    color:var(--yellow-words);
    opacity: 0.7;
    transition: all var(--animation-duration-2x);
}

body {
    color: var(--main-words) !important;
}

.welcomePanel .welcomeHeaderVersionDisplay {
    color:var(--main-words);
}
.welcomePanel .recentChatsTitle {
    color: var(--main-words);
}
.mes_button, .extraMesButtons > div {  filter: none;
    opacity: 1;
}

.extensions_info .extension_block {
background-color:var(--ui-background-main);
}
.regex-script-label {
  margin-right: 20px !important;
}
/*修改菜单栏点击后出现的折叠框的颜色*/
.drawer-content {
  background-color: var(--ui-background-main) !important; /* 设置折叠框的背景色为指定的浅米色 */
  border: 1px solid var(--ui-border-color) !important; /* 添加边框以增强视觉效果 */
  border-top: none !important; /* 去掉顶部边框，与菜单栏连接处无边框 */
  border-radius: 0 0 6px 6px; /* 底部圆角，匹配整体风格 */
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1) !important; /* 轻微阴影，提升立体感 */
}

#sheld {
    top: var(--bottomFormBlockSize);
    height: calc(100dvh - var(--bottomFormBlockSize));
}


/* ===== 标题样式修改 ===== */
/*标题添加虚线边框*/
.standoutHeader:not(h4),
#rm_PinAndTabs,
#top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p,
#rm_api_block .flex-container.alignItemsBaseline,
#title_api,
#AdvancedFormatting .flex-container.alignItemsBaseline:not(h3),
#WorldInfo .alignitemscenter:not(#world_popup *),
#user-settings-block div[name="userSettingsRowOne"],
#rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter) {
  background-image: none;
  border-radius: 4px;
  border: 4px dashed var(--green-words);
  margin-bottom: calc(var(--mainFontSize) * 0.4);
  padding: calc(var(--mainFontSize) * 0.2); /* 调整内边距，使文字与边框有间距 */
  transform: translateZ(0);
}

/*拓展、char界面char名边框左侧渲染*/
#rm_PinAndTabs,
#rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter) {
  will-change: transform, opacity;
}

/* 当角色名h2为空时，移除其父容器的边框和间距 */
#rm_PinAndTabs:has(#rm_button_selected_ch h2:empty) {
    border: none;
    margin-bottom: 0;
    padding: 0;
}
/*全局世界信息/知识书激活设置内*/
#wiSliders .alignitemscenter {
  border: none !important;  /* 移除边框 */
  background-image: none !important;
  border-radius: 0 !important;
  margin-bottom: 0 !important;
  padding: 0 !important;
  transform: none !important;
}
/* ===== 抽屉样式修改 ===== */
/* ---抽屉标题栏--- */
.inline-drawer-header:not(#WorldInfo .inline-drawer-header), /*排除世界书模块*/
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#top-settings-holder h4:not(#ai-config-button *):not(#rightNavHolder *) {
  position: relative;
  border: none !important;
  border-radius: 0;
  background-image: none !important;
  padding-left: 16px;
}


/* 左侧小三角形（排除世界书板块） */
.inline-drawer-header:not(#WorldInfo .inline-drawer-header)::before,
#extensions_settings .inline-drawer-toggle.inline-drawer-header::before,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header::before,
#top-settings-holder h4:not(#ai-config-button *):not(#rightNavHolder *)::before {
  content: "";
  position: absolute;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
  width: 0;
  height: 0;
  border-top: 5px solid transparent;
  border-bottom: 5px solid transparent;
  border-left: 8px solid var(--green-words);
}

/* 箭头图标颜色同步 */
.inline-drawer-icon {
  font-size: var(--icon-size);
  color: var(--green-words);
}

/* 缩小特定元素的三角形 */
.userSettingsInnerExpandable.inline-drawer-header::before,
.wide100p.flexFlowColumn.inline-drawer-header::before,
.inline-drawer[name="AutoCompleteToggle"] .inline-drawer-header::before {
  border-top: 3px solid transparent !important;   /* 高度缩小 */
  border-bottom: 3px solid transparent !important; /* 高度缩小 */
  border-left: 6px solid var(--green-words) !important; /* 宽度缩小 */
}
/* ===输入框=== */
/* 基础输入框【文本，数字，开关，未指定类型的输入框（默认文本输入），非搜索类型的文本域（textarea）】样式 */
input[type="text"],
input[type="number"],
input[type="switch"],
input:not([type]),
textarea:not([type="search"]) {
  color: var(--main-words) !important;
  background-color:  var(--ui-background-main) !important;
  border: 1px solid var(--ui-border-color) !important;
}

/*点击样式（没有变化）*/
input[type="text"]:focus,
input[type="number"]:focus,
input[type="switch"]:focus,
input:not([type]):focus,
textarea:not([type="search"]):focus {
  border: 1px solid var(--ui-border-color) !important;
}

/* 搜索框特殊处理 */
textarea[type="search"] {
  background-color: var(--ui-background-mainr) !important;
  border: none !important;
}

/* 范围滑块容器 */
.range-block {
  margin: calc(var(--mainFontSize) * 0.6) 0;
}

/* 范围滑块计数器 */
.range-block-counter {
  margin-left: 20px;
}

/* 特殊文本输入框 */
.text_pole:not(.objective-task) {
  background-color: rgba(254, 250, 239, 0.5);
  border: 1px solid var(--ui-border-color);
}
/*快捷回复输入栏*/
.popup:has(#qr--modalEditor) .popup-content > #qr--modalEditor > #qr--main > .qr--modal-messageContainer > #qr--modal-messageHolder > #qr--modal-message {
  color: var(--main-words)!important;
}
#send_textarea {
  clip-path: none !important;
  border-color: var(--ui-border-color) !important;
  border-radius: 6px;
}

#send_form {
    border: none;
    position: relative;
}

.popup:has(#qr--modalEditor) .popup-content > #qr--modalEditor > #qr--main > .qr--modal-messageContainer > #qr--modal-messageHolder > #qr--modal-message::selection {
    color:var(--main-words)!important;
}
/* ===滑块 === */
input[type="range"] {
 background: var(--yellow-words);
 filter: brightness(1);
}
.neo-range-slider{
 background: var(--yellow-words) !important;
 filter: brightness(1);
}
/* ===按键基础样式=== */
/*预设区不齐按钮对齐*/
.flex-container .margin0.menu_button {
    margin: 0;
}

.menu_button:not(.mes_edit_buttons *) {
  background-color: var(--yellow-words);
  color: var(--red-words);
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: all 0.3s ease;       /* 平滑过渡动画 */
}

.menu_button .mes_text i,
.menu_button .mes_text em {
  color: inherit;
}

/* ---悬停状态--- */
.menu_button:not(.mes_edit_buttons *):hover {
  background-color: var(--darkyellow);      /* 悬停时背景色加深 */
  transform: translateY(-1px);    /* 轻微上浮效果 */
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

/* ---选中/激活状态--- */
.menu_button:not(.mes_edit_buttons *):active,
.menu_button:not(.mes_edit_buttons *).is-active {
  background-color: var(--darkyellow) !important;  /* 选中状态颜色深黄 */
  color: white !important;
  box-shadow: inset 0 2px 4px rgba(0,0,0,0.2); /* 内阴影表示按下状态 */
}

/* ---高风险按钮（保持原样）--- */
#delete_button,
#dialogue_del_mes_ok,
#dialogue_popup_ok,
#bulk_tag_popup_reset,
#bulk_tag_popup_remove_mutual,
.mes_edit_cancel.menu_button,
.menu_button.popup-button-ok,
.redWarningBG {
  background-color: var(--crimson70a) !important;
  color: var(--white100) !important;
}

/* 几个没对齐的按钮微调 */
#openai_logit_bias_new_preset,
#openai_logit_bias_import_preset,
#openai_logit_bias_export_preset,
#openai_logit_bias_delete_preset {
  height: 31px !important;
}
#ui-preset-save-button,
#ui-preset-update-button {
  height: 30px !important;
}
#charListGridToggle,
#bulkEditButton{
  height: 28px !important;
}
/* ===代码块样式=== */
/* 基本<code>元素样式（适用于行内代码） */
code {
  margin: 0;                       /* 清除所有外边距 */
  border: 1px dashed var(--ui-border-color);
  border-border-radius: 5px;
  background-color: var(--ui-hover-color);
  padding: 0em 0.4em;           /* 上下内边距0.2em，左右内边距0.4em */
  color:var(--green-words);
}

/* 三反引号代码块*/
.hljs {
  background-color: var(--ui-hover-color);
  border: 1px dashed var(--ui-border-color);
  border-border-radius: 5px;
  padding: 0.8em;
  color: var(--green-words);
}
/* ==== 下拉框 ==== */
/*通用格式*/
select {
  background-color: var(--ui-background-main) !important;
  color: var(--main-words) !important;
  border: 1px solid var(--ui-border-color) !important;
  margin: calc(var(--mainFontSize) * 0.3) 0 !important;
}
/*​未选中选项​​*/
select option:not(:checked) {
    color: var(--main-words) !important;
}
/*​选中选项​​*/
select option:checked {
  color: var(--main-words)!important;
  background-color: var(--ui-hover-color) !important;
}
/*所有选项的背景颜色*/
select option {
  background-color: var(--ui-background-main) !important;
}
/* ===打勾框=== */
input[type="checkbox"] {
  width: calc(var(--mainFontSize) * 0.9);
  height: calc(var(--mainFontSize) * 0.9);
  border: none;
  outline: none;
  background-color: var(--ui-hover-color);
  filter: none;
  box-shadow: 0 0 0 1px var(--ui-border-color);
  transform: translateY(0);
  margin-left: 1px;
}

input[type="checkbox"]::before {
  box-shadow: inset 1em 1em var(--green-words);
}

input[type="checkbox"].del_checkbox {
  margin-right: 0;
}
/* ===滚动条=== */
/*竖直宽度*/
::-webkit-scrollbar {
  width: 4px;
}
/*水平宽度*/
::-webkit-scrollbar:horizontal {
  height: 4px;
}
/*背景颜色*/
::-webkit-scrollbar-track {
  background: transparent;
}
/*滚动条边框*/
::-webkit-scrollbar-thumb:vertical,
::-webkit-scrollbar-thumb:horizontal {
  background-color: var(--yellow-words);
  border:0.5px;
}
/* =====预设界面===== */
.title_restorable label {
  margin:0px !important;
}

/*预设大框修改*/
.text_pole, select, textarea {
  background-color: var(--ui-hover-color);
  border: 2px solid var(--ui-border-color);
  border-border-radius: 5px;
}
/*预设的条目的间距*/
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
align-items: center;
padding: 0.6em;
}
/*鼠标移动到条目时，条目会有轻微的上移的代码*/
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt:hover {
  transform: translateY(-3px); /* 向上移动3像素，模拟上浮 */
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2); /* 添加阴影，增强上浮效果 */
}
/*未激活预设条目*/
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled {
  border: 1px solid var(--ui-border-color);
  background-color: var(--ui-background-main);
  opacity: 0.4;
}
/*未激活条目的字改色*/
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
color: var(--main-words);
}
/*激活预设条目*/
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt {
  border: 1px solid var(--ui-border-color);
  border-border-radius: 5px;
  background-color: var(--ui-background-main);
}
/*条目文字前的符号*/
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt .completion_prompt_manager_prompt_name .fa-solid {
  color: var(--main-words);
}
/*未激活项目透明度降低*/
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt span span span {
  filter: none;
  opacity: 0.8;
}


/*===世界书模块===*/
/* 输入框整体样式 */
.select2-container .select2-selection--multiple,
.select2-container .select2-selection--single {
    background-color:var(--ui-background-main);
    border: 1px solid var(--ui-border-color);
    border-radius: 4px;
}

/* 输入框右侧的叉叉样式 */
.select2-container .select2-selection .select2-selection__clear {
    transition: transform 0.3s ease;
    color: var(--red-words);
}
/*世界书点开后的阴影*/
.select2-dropdown {
  box-shadow:none;
}
/*世界书标签左边的暗色*/
span.select2.select2-container .select2-selection__choice__remove {
    background-color: transparent !important;
}
/* 书名标签整体样式 */
.select2-container .select2-selection--multiple .select2-selection__choice,
.select2-container .select2-selection--single .select2-selection__choice {
    border-radius: 4px;
    border: none;
    box-sizing: none;
    color: var(--red-words);
    background-color:var(--yellow-words);
}

/* 书名标签右侧书名样式 */
.select2_choice_clickable_buttonstyle+span.select2-container .select2-selection__choice__display {
    background-color: transparent !important;
    color: var(--red-words) !important;
}
/*处理移动端左右滑动问题*/
@supports (-webkit-touch-callout: none) {
  #world_popup {
    margin-top: 0;
    width: 100%;
  }
}
/* 世界书条目未选择的阴影 - 修正和增强 */
.disabledWIEntry {
  opacity: 0.4; /* 保持未启用时的半透明效果 */
  filter: none; /* 确保默认没有滤镜 */
}

.disabledWIEntry:hover {
  filter: none; /* 确保悬停时也没有滤镜 */
  opacity: 1; /* 悬停时恢复完全不透明，以便看到 ui-hover-color */
  background-color: var(--ui-hover-color); /* 悬停时设置为 ui-hover-color */
}

/* 框距 */
.padding0 {
  padding: 5px;
}

/* 世界书条目整体样式 */
.wi-card-entry {
  background-color: var(--ui-hover-color);
  border-width: 1px;
  border-style: dashed;
  border-image: none;
  border-color: var(--ui-border-color);
  border-radius: 5px;
  padding: 0px 5px;
  margin-bottom: 5px;
  transition: transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out, opacity 0.2s ease-in-out, background-color 0.2s ease-in-out; /* 平滑过渡效果 */
}

/* 所有条目的悬停效果：上浮 */
.wi-card-entry:hover {
  transform: translateY(-3px); /* 向上移动3像素，模拟上浮 */
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2); /* 添加阴影，增强上浮效果 */
}
/*全局世界信息/知识书激活设置*/
.standoutHeader~.inline-drawer-content {
  background-color: var(--ui-hover-color);
}
/* ===== 用户角色卡片样式 ===== */
/* --- 通用悬停效果 --- */
#user_avatar_block .avatar-container:hover {
    transform: translateY(-2px) scale(1.01); /* 合并 transform 属性 */
    box-shadow: 0 4px 10px rgba(84, 74, 61, 0.32);
    transition: all 0.3s ease;
}
#user_avatar_block .avatar-container.selected {
    filter: brightness(1.1) contrast(1.05);
}
/* --- 通用边框样式 --- */
#user_avatar_block .avatar-container {
    background-color: var(--ui-hover-color);
    border: 2px dashed var(--ui-border-color);
    border-radius: 6px;
    box-sizing: border-box;
}


/* --- 默认角色特殊样式 --- */
#user_avatar_block .avatar-container.default_persona .ch_name {
    position: relative;
    padding-left: 18px;
    color: var(--green-words);
}

#user_avatar_block .avatar-container.default_persona .ch_name::before {
    content: "◉";
    position: absolute;
    left: 2px;
    top: 50%;
    transform: translateY(-50%);
}
#user_avatar_block .avatar-container.default_persona .avatar {
    outline: none !important;
    border: none !important;
    box-shadow: none !important;
}

/*====背景图片选择界面====*/
#form_bg_download .bg_example.no-border.no-shadow.add_bg_but.interactable {
    background-color: var(--ui-hover-color);
    background-image: url('/img/addbg3.png');
}

.add_bg_but {
    cursor: pointer;
    opacity: 0.5;
    height: 100%;
    width: 100%;
}
/* ===char界面 === */

#HotSwapWrapper {
  overflow: visible; /* 容器不裁剪内容 */
}

#HotSwapWrapper > div {
  overflow-y: auto;  /* 改为竖直滚动 */
  overflow-x: hidden;  /* 禁止水平滚动 */
  flex-wrap: wrap;  /* 允许换行（竖直布局需要） */
  height: calc(var(--avatar-base-height) + var(--scroll-width) + 10px);
  max-height: calc(var(--avatar-base-height) * 2);

  /* 确保滚动条始终可见 */
  scrollbar-width: thin;
  scrollbar-color: var(--ui-hover-color) transparent;
}

/* 所有角色头像统一样式​​ */
#rm_print_characters_block .bogus_folder_select.entity_block .avatar img,
#rm_print_characters_block .character_select.entity_block .avatar img,
#rm_print_characters_block .group_select.entity_block .avatar img,
.hotswap .avatar img {
    border: none;
}

/*角色/群组/文件夹的块状框主体样式*/
#rm_print_characters_block .bogus_folder_select.entity_block,
#rm_print_characters_block .character_select.entity_block,
#rm_print_characters_block .group_select.entity_block {
    width: 100%; /* 改为100%父容器宽度 */
    margin: 0 0 12px 0; /* 仅保留底部外边距 */
    box-sizing: border-box; /* 确保padding不额外增加宽度 */

    /* === 背景与边框 === */
    background-color: var(--ui-hover-color) !important;
border: 2px dashed var(--green-words);
    border-radius: 8px; /* 稍大的圆角 */
    box-shadow:
        0 2px 6px rgba(84, 74, 61, 0.22), /* 柔和的下阴影 */
        inset 0 0 10px rgba(85, 25, 21, 0.05); /* 内敛的深色内阴影 */

    /* === 文字样式 === */
    color: var(--main-words) !important;
    font-size: 15px;
    letter-spacing: 0.5px;
    line-height: 1.4;
    padding: 8px 8px; /* 增大内边距 */
    position: relative;

    /* === 过渡动画 === */
    transition:
        transform 0.2s ease,
        box-shadow 0.3s ease;
}


/* 悬停状态增强 */
#rm_print_characters_block .bogus_folder_select.entity_block:hover,
#rm_print_characters_block .character_select.entity_block:hover,
#rm_print_characters_block .group_select.entity_block:hover {
    transform: translateY(-2px);
    box-shadow:
        0 4px 10px rgba(84, 74, 61, 0.32),
        inset 0 0 12px rgba(85, 25, 21, 0.08);
}

/* 选中状态 */
#rm_print_characters_block .entity_block.character_selected {
    background-color: var(--ui-hover-color) !important;
