/*====聊天界面====*/

/*气泡基础样式*/
.mes {
    position: relative;
    margin: 5px 0 ; /* 两条消息的间距 */
    border-radius: 4px; /* 圆角 */
}
/* 用户消息特殊样式（边框做颜色区分）*/
div[is_user="true"] {
  border: 1px solid var(--yellow-words) !important;
  border-left: 3px solid var(--yellow-words) !important;
}

/* 角色消息特殊样式 */
div[is_user="false"] {
  border: 1px solid var(--red-words) !important; /* 四条边统一边框 */
  border-left: 3px solid var(--red-words) !important; /* 左侧边框加粗 */
}

/* 头像位置 - 增强稳定性 */
.mes .avatar {
    position: absolute !important;
    top: 20px !important;
    left: 20px !important;
    z-index: 10 !important;
    width: 60px !important; /* 固定头像宽度 */
    height: 90px !important; /* 固定头像高度 */
}

/* 确保头像图片不会影响布局 */
.mes .avatar img {
    width: 100% !important;
    height: 100% !important;
    object-fit: cover !important;
}

/* 名字位置 - 增强定位稳定性 */
.mes .name_text {
    position: absolute !important;
    top: 15px !important;
    left: 90px !important;
    font-family: Microsoft Yahei !important;
    font-size: 1.2em !important;
    margin: 0 !important;
    padding: 0 !important;
    line-height: 1.2 !important;
    width: calc(100% - 110px) !important; /* 防止文字溢出 */
    z-index: 5 !important;
}

/* Token计数位置 - 确保不与其他元素重叠 */
.tokenCounterDisplay {
    position: absolute !important;
    left: 100px !important;
    top: 50px !important;
    opacity: 1 !important;
    margin: 0 !important;
    padding: 0 !important;
    display: block !important;
    z-index: 5 !important;
    font-size: 0.9em !important;
}

/* 时间计数位置 - 调整以避免重叠 */
.mes_timer {
    position: absolute !important;
    left: 145px !important; /* 增加左边距避免与token重叠 */
    top: 50px !important;
    opacity: 1 !important;
    padding: 0 !important;
    margin: 0 !important;
    display: block !important;
    z-index: 5 !important;
    font-size: 0.9em !important;
}

/* 聊天时间样式 - 确保位置稳定 */
.mes .timestamp {
    position: absolute !important;
    left: 90px !important;
    top: 60px !important;
    color: var(--main-words) !important;
    opacity: 1 !important;
    font-size: 0.8em !important;
    margin: 0 !important;
    padding: 0 !important;
    display: block !important;
    z-index: 5 !important;
}

/* 楼层计数样式 - 防止位置漂移 */
.mesIDDisplay {
    position: absolute !important;
    top: 90px !important;
    left: 100px !important;
    opacity: 1 !important;
    margin: 0 !important;
    padding: 0 !important;
    display: block !important;
    z-index: 5 !important;
    font-size: 0.9em !important;
}

/* 聊天内容空间调整 - 确保为头部信息留出足够空间 */
.mes_block {
    padding-top: 80px !important; /* 增加顶部空间 */
    padding-bottom: 10px !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
    position: relative !important;
    z-index: 1 !important;
}

/* 聊天内容文本区域 */
.mes_text {
    padding-right: 3px !important;
    position: relative !important;
    z-index: 1 !important;
    margin: 0 !important;
}

/* 聊天内容编辑图标 */
.mes .mes_button,
.mes .mes .extraMesButtons>div {
    filter: none !important;
    opacity: 1 !important;
    font-size: 0.8em !important;
    color: var(--main-words) !important;
}

/* 悬停小图标颜色变暗 */
.mes_button:hover, .extraMesButtons>div:hover {
    filter: brightness(0.9) !important;
}
.mes_edit_buttons {
    filter: none;
}

/* 防止元素重叠的额外保护 */
.mes::before {
    content: "";
    display: block;
    height: 105px; /* 为头部信息预留空间 */
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 0;
    pointer-events: none;
}

/* 幽灵图标样式 - 定位到右上角 */
.mes_ghost {
    position: absolute;
    top: 5px;
    right: 5px;
    font-size: 1.2em;
    color: var(--main-words); /* 设置图标颜色*/
    z-index: 15; /* 确保图标在其他内容之上 */
}
/* === 主页最新消息 === */
/*主体卡片样式*/
.recentChat {
    background-color: var(--ui-hover-color) !important;
    border: 2px dashed var(--green-words) !important;
    border-radius: 8px;
    box-shadow:
        0 2px 6px rgba(84, 74, 61, 0.22),
        inset 0 0 10px rgba(85, 25, 21, 0.05);
    color: var(--main-words) !important;
    font-size: 15px;
    padding: 12px 16px;
    margin-bottom: 10px;
    display: flex;
    gap: 14px;
    align-items: flex-start;
    transition: background 0.3s ease, transform 0.2s ease;
    position: relative;
}

.recentChat:hover {
    transform: translateY(-2px);
    box-shadow:
        0 4px 10px rgba(84, 74, 61, 0.32),
        inset 0 0 12px rgba(85, 25, 21, 0.08);
    transform: scale(1.01);
}



/* 隐藏聊天预览文字（chatMessage 区域） */
.recentChat .chatMessage {
    display: none !important;
/* 完全隐藏消息内容，只保留名字/时间/统计 */
}

/* 设置聊天信息区域的垂直布局（名字 -> 时间 -> 统计） */
.recentChat .chatNameContainer {
    display: flex !important;
    flex-direction: column !important;
    align-items: flex-start !important;
    justify-content: flex-end !important;
    height: 100%; /* 允许元素填满高度 */
    gap:0px !important;
    box-sizing: border-box;
}

/*名字样式 */
.recentChat .chatName {
    font-size: 16px !important;
    font-weight: bold;
color: var(--main-words) !important;
    line-height: 1.2;
}

/* 时间样式 */
.recentChat .chatDate {
    position: static !important;
/* 确保参与垂直排列 */
    font-size: 14px !important;
/* ← 和消息预览一致 */
    color: var(--main-words) !important;
    margin: 0 !important;
    padding: 0 !important;
}

/* 消息统计贴底*/
.recentChat .chatStats {
    font-size: 14px !important;
    color: var(--yellow-words);
    margin-top: 20px!important;
}
/*思维链折叠*/
.mes_reasoning_header {
  background-color: var(--ui-hover-color);
  border: 1px dashed var(--ui-border-color);
}
